package week1.day2;

public class Evenorodd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int input =  50;
		if(input%2 == 0 )
		{
			System.out.println("The given number is even");
			
		}else {
			System.out.println("The given number is odd");
		}
 
	}

}
